mapper.select:
select    
from tb_peoples s
where s.community_id = #{communityId}

select    
from tb_peoples s
where s.open_id=#{openId}

mapper.insert:
insert into tb_peoples (uuid, nickname, truename, 
  head_pic, mobile, sex, 
  password, is_real_name, id_card, 
  community_id, total_integral, integral, 
  status, create_time, update_time
  )
values (#{uuid,jdbcType=VARCHAR}, #{nickname,jdbcType=VARCHAR}, #{truename,jdbcType=VARCHAR}, #{headPic,jdbcType=VARCHAR}, #{mobile,jdbcType=VARCHAR}, #{sex,jdbcType=INTEGER}, #{password,jdbcType=VARCHAR}, #{isRealName,jdbcType=INTEGER}, #{idCard,jdbcType=VARCHAR}, #{communityId,jdbcType=VARCHAR}, #{totalIntegral,jdbcType=INTEGER}, #{integral,jdbcType=INTEGER}, #{status,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )
